function showDropdown(n){const i=document.getElementById(n+"Button"),t=document.getElementById(n+"Content"),r=t.classList.contains("show"),u=document.querySelectorAll(".footer__dropdown-content.show");u.forEach(n=>n.classList.remove("show"));const f=document.querySelectorAll(".footer__dropdown-button.active");f.forEach(n=>n.classList.remove("active"));r||(t.classList.add("show"),i.classList.add("active"))}